mapper.insert:
INSERT INTO PROD_OPT (PROD_ID
                     , OPT_PRC  /* 옵션가 */
                     , INV_QTY  /* 재고수량 */
                     , REGR_ID)
VALUES (#{prodId}
       , #{optPrc}
       , #{invQty}
       , #{regrId} );

mapper.select:
SELECT PO.PROD_ID, PO.OPT_COMB_NO, PO.INV_QTY
FROM PROD_OPT PO
WHERE OPT_COMB_NO IN

    #{optCombNoArr}

FOR UPDATE NOWAIT;

mapper.update:
UPDATE prod_opt
SET INV_QTY = INV_QTY - #{qty}
  , UPD_DTTM = now()
WHERE OPT_COMB_NO = #{optCombNo}

UPDATE prod_opt
SET INV_QTY = #{invQty}
  , UPD_DTTM = now()
WHERE OPT_COMB_NO = #{optCombNo}
